	// implement IXmlReadable
}

public class MyParent : IXmlReadable {

	public List<MyChild> Children { get; set; }
	
	public void WriteXml (XmlElement element) {
		element.WriteChildListXml<MyChild>(this.Children, "mychild", "children");
	}
	
